import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { CreateBeneficiarioRequest } from 'src/app/models/interfaces/beneficiario/request/CreateBeneficiarioRequest';
import { GetBeneficiarioResponse } from 'src/app/models/interfaces/beneficiario/responses/GetBeneficiarioResponse';
import { GetAllBeneficiarioResponse } from 'src/app/models/interfaces/beneficiario/responses/GetAllBeneficiarioResponse';
import { EditBeneficiarioRequest } from 'src/app/models/interfaces/beneficiario/request/EditBeneficiarioRequest';

@Injectable({
  providedIn: 'root'
})
export class BeneficiarioService {
  private readonly apiUrl = 'http://localhost:3000';

  constructor(private http: HttpClient) {}

  getAllBeneficiarios(): Observable<GetAllBeneficiarioResponse[]> {
    return this.http.get<GetAllBeneficiarioResponse[]>(`${this.apiUrl}`);
  }

  getBeneficiarioById(id: string): Observable<GetBeneficiarioResponse> {
    return this.http.get<GetBeneficiarioResponse>(`${this.apiUrl}/${id}`);
  }

  createBeneficiario(request: CreateBeneficiarioRequest): Observable<any> {
    return this.http.post(`${this.apiUrl}`, request);
  }

  editBeneficiario(request: EditBeneficiarioRequest): Observable<any> {
    return this.http.put(`${this.apiUrl}/${request.beneficiario_id}`, request);
  }

  deleteBeneficiario(id: string): Observable<any> {
    return this.http.delete(`${this.apiUrl}/${id}`);
  }
}
