import { Component, OnDestroy, OnInit } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { MessageService } from 'primeng/api';
import { DynamicDialogConfig } from 'primeng/dynamicdialog';
import { Subject, takeUntil } from 'rxjs';
import { BeneficiarioEvent } from 'src/app/models/enums/beneficiario/BeneficiarioEvent';
import { GetCategoriesResponse } from 'src/app/models/interfaces/categories/responses/GetCategoriesResponse';
import { EventAction } from 'src/app/models/interfaces/beneficiario/event/EventAction';
import { CreateBeneficiarioRequest } from 'src/app/models/interfaces/beneficiario/request/CreateBeneficiarioRequest';
import { EditBeneficiarioRequest } from 'src/app/models/interfaces/beneficiario/request/EditBeneficiarioRequest';
import { GetAllBeneficiarioResponse } from 'src/app/models/interfaces/beneficiario/responses/GetAllBeneficiarioResponse';
import { CategoriesService } from 'src/app/services/categories/categories.service';
import { BeneficiariosService } from 'src/app/services/beneficiario/beneficiario.service';
import { BeneficiariosDataTransferService } from 'src/app/shared/services/beneficiarios/beneficiarios-data-transfer.service';

@Component({
  selector: 'app-beneficiario-form',
  templateUrl: './beneficiario-form.component.html',
  styleUrls: [],
})
export class BeneficiarioFormComponent implements OnInit, OnDestroy {
  private readonly destroy$: Subject<void> = new Subject();
  public categoriesDatas: Array<GetCategoriesResponse> = [];
  public selectedCategory: Array<{ name: string; code: string }> = [];
  public beneficiarioAction!: {
    event: EventAction;
    beneficiarioDatas: Array<GetAllBeneficiarioResponse>;
  };
  public beneficiarioSelectedDatas!: GetAllBeneficiarioResponse;
  public beneficiariosDatas: Array<GetAllBeneficiarioResponse> = [];
  public addBeneficiarioForm = this.formBuilder.group({
    nome: ['', Validators.required],
    datanasc: [null],
    tipopessoa: ['F', Validators.required],
    genero: [null],
    cpf: [''],
    cnpj: [''],
    contato1: [''],
    contato2: [''],
    cep: [''],
    logradoudo: [''],
    numero: [''],
    complemento: [''],
    bairro: [''],
    cidade: [''],
    siglaestado: [''],
    observacoes: ['']
  });
  public editBeneficiarioForm = this.formBuilder.group({
    nome: ['', Validators.required],
    datanasc: [null],
    tipopessoa: ['F', Validators.required],
    genero: [null],
    cpf: [''],
    cnpj: [''],
    contato1: [''],
    contato2: [''],
    cep: [''],
    logradoudo: [''],
    numero: [''],
    complemento: [''],
    bairro: [''],
    cidade: [''],
    siglaestado: [''],
    observacoes: ['']
  });

  public addBeneficiarioAction = BeneficiarioEvent.ADD_BENEFICIARIO_ACTION;
  public editBeneficiarioAction = BeneficiarioEvent.EDIT_BENEFICIARIO_ACTION;

  constructor(
    private categoriesService: CategoriesService,
    private beneficiariosService: BeneficiariosService,
    private beneficiariosDtService: BeneficiariosDataTransferService,
    private formBuilder: FormBuilder,
    private messageService: MessageService,
    private router: Router,
    public ref: DynamicDialogConfig
  ) {}

  ngOnInit(): void {
    this.beneficiarioAction = this.ref.data;

    if (
      this.beneficiarioAction?.event?.action === this.editBeneficiarioAction &&
      this.beneficiarioAction?.beneficiarioDatas
    ) {
      this.getBeneficiarioSelectedDatas(this.beneficiarioAction?.event?.id as string);
    }

    this.getAllCategories();
  }

  getAllCategories(): void {
    this.categoriesService
      .getAllCategories()
      .pipe(takeUntil(this.destroy$))
      .subscribe({
        next: (response) => {
          if (response.length > 0) {
            this.categoriesDatas = response;
          }
        },
      });
  }

  handleSubmitAddBeneficiario(): void {
    if (this.addBeneficiarioForm?.value && this.addBeneficiarioForm?.valid) {
      const requestCreateBeneficiario: CreateBeneficiarioRequest = {
        nome: this.addBeneficiarioForm.value.nome as string,
        datanasc: this.addBeneficiarioForm.value.datanasc as Date,
        tipopessoa: this.addBeneficiarioForm.value.tipopessoa as string,
        genero: this.addBeneficiarioForm.value.genero as string,
        cpf: this.addBeneficiarioForm.value.cpf as string,
        cnpj: this.addBeneficiarioForm.value.cnpj as string,
        contato1: this.addBeneficiarioForm.value.contato1 as string,
        contato2: this.addBeneficiarioForm.value.contato2 as string,
        cep: this.addBeneficiarioForm.value.cep as string,
        logradoudo: this.addBeneficiarioForm.value.logradoudo as string,
        numero: this.addBeneficiarioForm.value.numero as string,
        complemento: this.addBeneficiarioForm.value.complemento as string,
        bairro: this.addBeneficiarioForm.value.bairro as string,
        cidade: this.addBeneficiarioForm.value.cidade as string,
        siglaestado: this.addBeneficiarioForm.value.siglaestado as string,
        observacoes: this.addBeneficiarioForm.value.observacoes as string,
      };

      this.beneficiariosService
        .createBeneficiario(requestCreateBeneficiario)
        .pipe(takeUntil(this.destroy$))
        .subscribe({
          next: (response) => {
            if (response) {
              this.messageService.add({
                severity: 'success',
                summary: 'Sucesso',
                detail: 'Beneficiário criado com sucesso!',
                life: 2500,
              });
            }
          },
          error: (err) => {
            console.log(err);
            this.messageService.add({
              severity: 'error',
              summary: 'Erro',
              detail: 'Erro ao criar beneficiário!',
              life: 2500,
            });
          },
        });
    }

    this.addBeneficiarioForm.reset();
  }

  handleSubmitEditBeneficiario(): void {
    if (
      this.editBeneficiarioForm.value &&
      this.editBeneficiarioForm.valid &&
      this.beneficiarioAction.event.id
    ) {
      const requestEditBeneficiario: EditBeneficiarioRequest = {
        nome: this.editBeneficiarioForm.value.nome as string,
        datanasc: this.editBeneficiarioForm.value.datanasc as Date,
        tipopessoa: this.editBeneficiarioForm.value.tipopessoa as string,
        genero: this.editBeneficiarioForm.value.genero as string,
        cpf: this.editBeneficiarioForm.value.cpf as string,
        cnpj: this.editBeneficiarioForm.value.cnpj as string,
        contato1: this.editBeneficiarioForm.value.contato1 as string,
        contato2: this.editBeneficiarioForm.value.contato2 as string,
        cep: this.editBeneficiarioForm.value.cep as string,
        logradoudo: this.editBeneficiarioForm.value.logradoudo as string,
        numero: this.editBeneficiarioForm.value.numero as string,
        complemento: this.editBeneficiarioForm.value.complemento as string,
        bairro: this.editBeneficiarioForm.value.bairro as string,
        cidade: this.editBeneficiarioForm.value.cidade as string,
        siglaestado: this.editBeneficiarioForm.value.siglaestado as string,
        observacoes: this.editBeneficiarioForm.value.observacoes as string,
        beneficiario_id: this.beneficiarioAction.event.id,
      };

      this.beneficiariosService
        .editBeneficiario(requestEditBeneficiario)
        .pipe(takeUntil(this.destroy$))
        .subscribe({
          next: () => {
            this.messageService.add({
              severity: 'success',
              summary: 'Sucesso',
              detail: 'Beneficiário editado com sucesso!',
              life: 2500,
            });
            this.editBeneficiarioForm.reset();
          },
          error: (err) => {
            console.log(err);
            this.messageService.add({
              severity: 'error',
              summary: 'Erro',
              detail: 'Erro ao editar beneficiário!',
              life: 2500,
            });
            this.editBeneficiarioForm.reset();
          },
        });
    }
  }

  getBeneficiarioSelectedDatas(beneficiarioId: string): void {
    const allBeneficiarios = this.beneficiarioAction?.beneficiarioDatas;

    if (allBeneficiarios.length > 0) {
      const beneficiarioFiltered = allBeneficiarios.filter(
        (element) => element?.id === beneficiarioId
      );

      if (beneficiarioFiltered) {
        this.beneficiarioSelectedDatas = beneficiarioFiltered[0];

        this.editBeneficiarioForm.setValue({
          nome: this.beneficiarioSelectedDatas?.nome,
          datanasc: this.beneficiarioSelectedDatas?.datanasc,
          tipopessoa: this.beneficiarioSelectedDatas?.tipopessoa,
          genero: this.beneficiarioSelectedDatas?.genero,
          cpf: this.beneficiarioSelectedDatas?.cpf,
          cnpj: this.beneficiarioSelectedDatas?.cnpj,
          contato1: this.beneficiarioSelectedDatas?.contato1,
          contato2: this.beneficiarioSelectedDatas?.contato2,
          cep: this.beneficiarioSelectedDatas?.cep,
          logradoudo: this.beneficiarioSelectedDatas?.logradoudo,
          numero: this.beneficiarioSelectedDatas?.numero,
          complemento: this.beneficiarioSelectedDatas?.complemento,
          bairro: this.beneficiarioSelectedDatas?.bairro,
          cidade: this.beneficiarioSelectedDatas?.cidade,
          siglaestado: this.beneficiarioSelectedDatas?.siglaestado,
          observacoes: this.beneficiarioSelectedDatas?.observacoes,
        });
      }
    }
  }

  ngOnDestroy(): void {
    this.destroy$.next();
    this.destroy$.complete();
  }
}
