import { Component, OnDestroy, OnInit } from '@angular/core';
import { FormBuilder, Validators, FormGroup } from '@angular/forms';
import { MessageService } from 'primeng/api';
import { DynamicDialogConfig } from 'primeng/dynamicdialog';
import { Subject } from 'rxjs';
import { takeUntil } from 'rxjs/operators';
import { BeneficiarioService } from 'src/app/services/beneficiario/beneficiario.service';
import { GetBeneficiarioResponse } from 'src/app/models/interfaces/beneficiario/responses/GetBeneficiarioResponse';
import { EditBeneficiarioAction } from 'src/app/models/interfaces/beneficiario/event/EditBeneficiarioAction';

@Component({
  selector: 'app-beneficiario-form',
  templateUrl: './beneficiario-form.component.html',
  styleUrls: []
})
export class BeneficiarioFormComponent implements OnInit, OnDestroy {
  private readonly destroy$: Subject<void> = new Subject();

  public beneficiarioForm: FormGroup;
  public isEditing = false; 
  public estados: any[]; 
  public tiposPessoa: any[]; 
  public generos: any[]; 

  constructor(
    public ref: DynamicDialogConfig,
    private formBuilder: FormBuilder,
    private messageService: MessageService,
    private beneficiarioService: BeneficiarioService
  ) {
    this.beneficiarioForm = this.formBuilder.group({
      nome: ['', Validators.required],
      tipoPessoa: ['F', Validators.required], 
      cpf: [''],
      rg: [''],
      genero: ['', Validators.required],
      dataNascimento: [''],
      cnpj: [''],
      razaoSocial: [''], 
      contato1: ['', Validators.required],
      contato2: [''],
      cep: ['', Validators.required],
      logradouro: ['', Validators.required],
      numero: ['', Validators.required],
      complemento: ['', Validators.required],
      bairro: ['', Validators.required],
      cidade: ['', Validators.required],
      siglaEstado: ['', Validators.required],
      observacoes: [''],
      ativo: [true] 
    });


    this.estados = [
      { label: 'Acre', value: 'AC' },
      { label: 'Alagoas', value: 'AL' },
      { label: 'Amapá', value: 'AP' },
      { label: 'Amazonas', value: 'AM' },
      { label: 'Bahia', value: 'BA' },
      { label: 'Ceará', value: 'CE' },
      { label: 'Distrito Federal', value: 'DF' },
      { label: 'Espírito Santo', value: 'ES' },
      { label: 'Goiás', value: 'GO' },
      { label: 'Maranhão', value: 'MA' },
      { label: 'Mato Grosso', value: 'MT' },
      { label: 'Mato Grosso do Sul', value: 'MS' },
      { label: 'Minas Gerais', value: 'MG' },
      { label: 'Pará', value: 'PA' },
      { label: 'Paraíba', value: 'PB' },
      { label: 'Paraná', value: 'PR' },
      { label: 'Pernambuco', value: 'PE' },
      { label: 'Piauí', value: 'PI' },
      { label: 'Rio de Janeiro', value: 'RJ' },
      { label: 'Rio Grande do Norte', value: 'RN' },
      { label: 'Rio Grande do Sul', value: 'RS' },
      { label: 'Rondônia', value: 'RO' },
      { label: 'Roraima', value: 'RR' },
      { label: 'Santa Catarina', value: 'SC' },
      { label: 'São Paulo', value: 'SP' },
      { label: 'Sergipe', value: 'SE' },
      { label: 'Tocantins', value: 'TO' }
    ];

    // Lista de tipos de pessoa
    this.tiposPessoa = [
      { label: 'Pessoa Física', value: 'F' },
      { label: 'Pessoa Jurídica', value: 'J' }
    ];

    // Lista de gêneros
    this.generos = [
      { label: 'Masculino', value: 'M' },
      { label: 'Feminino', value: 'F' }
    ];
  }

  ngOnInit(): void {
    const beneficiarioData = this.ref.data;

    if (beneficiarioData) {
      this.isEditing = !!beneficiarioData.idbeneficiario; // Verifica se há um ID para determinar se é edição
      this.populateForm(beneficiarioData);
    }
  }

  handleSubmit(): void {
    if (this.beneficiarioForm.valid) {
      const formData = this.beneficiarioForm.value;

      if (this.isEditing) {
        this.editBeneficiario(formData);
      } else {
        this.addBeneficiario(formData);
      }
    } else {
      this.handleErrorMessage('Formulário inválido. Verifique os campos obrigatórios.');
    }
  }

  private addBeneficiario(formData: any): void {
    this.beneficiarioService['createBeneficiario'](formData).pipe(
      takeUntil(this.destroy$)
    ).subscribe({
      next: () => {
        this.handleSuccessMessage('Beneficiário criado com sucesso!');
      },
      error: () => {
        this.handleErrorMessage('Erro ao criar beneficiário!');
      }
    });
  }

  private editBeneficiario(formData: any): void {
    const editAction: EditBeneficiarioAction = {
      action: 'edit',
      id: formData.idbeneficiario, // Supondo que o ID seja parte do formulário ou recuperado de outra forma
      beneficiarioName: formData.nome
    };

    this.beneficiarioService['editBeneficiario'](editAction).pipe(
      takeUntil(this.destroy$)
    ).subscribe({
      next: () => {
        this.handleSuccessMessage('Beneficiário editado com sucesso!');
      },
      error: () => {
        this.handleErrorMessage('Erro ao editar beneficiário!');
      }
    });
  }

  private populateForm(beneficiarioData: GetBeneficiarioResponse): void {
    this.beneficiarioForm.patchValue({
      nome: beneficiarioData.nome,
      tipoPessoa: beneficiarioData.tipoPessoa, // Certifique-se de usar o nome correto da propriedade do tipo
      cpf: beneficiarioData.cpf,
      rg: beneficiarioData.rg,
      genero: beneficiarioData.genero,
      dataNascimento: beneficiarioData.dataNascimento,
      cnpj: beneficiarioData.cnpj,
      razaoSocial: beneficiarioData.razaoSocial,
      contato1: beneficiarioData.contato1,
      contato2: beneficiarioData.contato2,
      cep: beneficiarioData.cep,
      logradouro: beneficiarioData.logradouro,
      numero: beneficiarioData.numero,
      complemento: beneficiarioData.complemento,
      bairro: beneficiarioData.bairro,
      cidade: beneficiarioData.cidade,
      siglaEstado: beneficiarioData.siglaEstado, // Certifique-se de usar o nome correto da propriedade do tipo
      observacoes: beneficiarioData.observacoes,
      ativo: beneficiarioData.ativo
    });
  }

  private handleSuccessMessage(detail: string): void {
    this.beneficiarioForm.reset();
    this.messageService.add({
      severity: 'success',
      summary: 'Sucesso',
      detail: detail,
      life: 3000
    });
  }

  private handleErrorMessage(detail: string): void {
    this.messageService.add({
      severity: 'error',
      summary: 'Erro',
      detail: detail,
      life: 3000
    });
  }

  ngOnDestroy(): void {
    this.destroy$.next();
    this.destroy$.complete();
  }
}
<div>
  <form [formGroup]="beneficiarioForm" (ngSubmit)="handleSubmit()">
    <div class="card flex flex-column md:flex-column gap-3">
      <input pInputText placeholder="Nome" formControlName="nome" />

      <p-dropdown 
        [options]="tiposPessoa" 
        formControlName="tipoPessoa" 
        placeholder="Selecione o Tipo de Pessoa">
      </p-dropdown>

      <p-dropdown 
      [options]="estados" 
      formControlName="siglaEstado" 
      placeholder="Selecione um Estado">
    </p-dropdown>

      <div *ngIf="beneficiarioForm.get('tipoPessoa')?.value === 'F'">
        <input style="margin-right: 13px!important" type="number" maxlength="11" pInputText placeholder="CPF" formControlName="cpf" />
        <input style="margin-right: 13px!important" maxlength="9" pInputText placeholder="RG" formControlName="rg" />
        <input style="margin-right: 13px!important" type="date" pInputText placeholder="Data de Nascimento" formControlName="dataNascimento" />
        <p-dropdown 
          [options]="generos" 
          formControlName="genero" 
          placeholder="Selecione o Gênero">
        </p-dropdown>

      </div>

      <div *ngIf="beneficiarioForm.get('tipoPessoa')?.value === 'J'">
        <input pInputText style="margin-right: 13px!important" type="number" placeholder="CNPJ" maxlength="14" formControlName="cnpj" />
        <input pInputText placeholder="Razão Social" formControlName="razaoSocial" />
      </div>

      <input pInputText placeholder="Contato 1 " maxlength="11" type="number" formControlName="contato1" />
      <input pInputText placeholder="Contato 2" maxlength="11" type="number"  formControlName="contato2" />
      <input pInputText placeholder="CEP" maxlength="8"  formControlName="cep" />
      <input pInputText placeholder="Rua" formControlName="logradouro" />
      <input pInputText placeholder="Número"  formControlName="numero" />
      <input pInputText placeholder="Complemento" formControlName="complemento" />
      <input pInputText placeholder="Bairro" formControlName="bairro" />
      <input pInputText placeholder="Cidade" formControlName="cidade" />



      <textarea pInputTextarea placeholder="Observações" formControlName="observacoes"></textarea>

      <div class="flex flex-row justify-content-center align-content-center align-items-center">
        <p-button [disabled]="!beneficiarioForm.valid" type="submit" label="Concluir"></p-button>
      </div>
    </div>
  </form>
</div>
