import { Component, OnDestroy, OnInit } from '@angular/core';
import { FormBuilder, Validators, FormGroup } from '@angular/forms';
import { MessageService } from 'primeng/api';
import { DynamicDialogConfig } from 'primeng/dynamicdialog';
import { Subject } from 'rxjs';
import { takeUntil } from 'rxjs/operators';
import { DoadorService } from 'src/app/services/doador/doador.service';
import { GetDoadorResponse } from 'src/app/models/interfaces/doador/responses/GetDoadorResponse';
import { EditDoadorAction } from 'src/app/models/interfaces/doador/event/EditDoadorAction';

@Component({
  selector: 'app-doador-form',
  templateUrl: './doador-form.component.html',
  styleUrls: []
})
export class DoadorFormComponent implements OnInit, OnDestroy {
  private readonly destroy$: Subject<void> = new Subject();

  public doadorForm: FormGroup;
  public isEditing = false; // Flag para verificar se está editando um doador existente
  public estados: any[]; // Lista de estados
  public tiposPessoa: any[]; // Lista de tipos de pessoa
  public generos: any[]; // Lista de gêneros

  constructor(
    public ref: DynamicDialogConfig,
    private formBuilder: FormBuilder,
    private messageService: MessageService,
    private doadorService: DoadorService
  ) {
    this.doadorForm = this.formBuilder.group({
      nome: ['', Validators.required],
      tipoPessoa: ['F', Validators.required], // Valor padrão 'F' para pessoa física
      cpf: [''],
      rg: [''],
      genero: [''],
      dataNascimento: [''],
      cnpj: [''],
      razaoSocial: [''],
      contato1: [''],
      contato2: [''],
      cep: [''],
      logradouro: [''],
      numero: [''],
      complemento: [''],
      bairro: [''],
      cidade: [''],
      siglaEstado: ['', Validators.required],
      observacoes: [''],
      ativo: [true] // Valor padrão 'true' para ativo
    });

    // Lista de estados do Brasil
    this.estados = [
      { label: 'Acre', value: 'AC' },
      { label: 'Alagoas', value: 'AL' },
      { label: 'Amapá', value: 'AP' },
      { label: 'Amazonas', value: 'AM' },
      { label: 'Bahia', value: 'BA' },
      { label: 'Ceará', value: 'CE' },
      { label: 'Distrito Federal', value: 'DF' },
      { label: 'Espírito Santo', value: 'ES' },
      { label: 'Goiás', value: 'GO' },
      { label: 'Maranhão', value: 'MA' },
      { label: 'Mato Grosso', value: 'MT' },
      { label: 'Mato Grosso do Sul', value: 'MS' },
      { label: 'Minas Gerais', value: 'MG' },
      { label: 'Pará', value: 'PA' },
      { label: 'Paraíba', value: 'PB' },
      { label: 'Paraná', value: 'PR' },
      { label: 'Pernambuco', value: 'PE' },
      { label: 'Piauí', value: 'PI' },
      { label: 'Rio de Janeiro', value: 'RJ' },
      { label: 'Rio Grande do Norte', value: 'RN' },
      { label: 'Rio Grande do Sul', value: 'RS' },
      { label: 'Rondônia', value: 'RO' },
      { label: 'Roraima', value: 'RR' },
      { label: 'Santa Catarina', value: 'SC' },
      { label: 'São Paulo', value: 'SP' },
      { label: 'Sergipe', value: 'SE' },
      { label: 'Tocantins', value: 'TO' }
    ];

    // Lista de tipos de pessoa
    this.tiposPessoa = [
      { label: 'Pessoa Física', value: 'F' },
      { label: 'Pessoa Jurídica', value: 'J' }
    ];

    // Lista de gêneros
    this.generos = [
      { label: 'Masculino', value: 'M' },
      { label: 'Feminino', value: 'F' },
      { label: 'Outro', value: 'O' }
    ];
  }

  ngOnInit(): void {
    const doadorData = this.ref.data;

    if (doadorData) {
      this.isEditing = !!doadorData.id; // Verifica se há um ID para determinar se é edição
      this.populateForm(doadorData);
    }
  }

  handleSubmit(): void {
    if (this.doadorForm.valid) {
      const formData = this.doadorForm.value;

      if (this.isEditing) {
        this.editDoador(formData);
      } else {
        this.addDoador(formData);
      }
    } else {
      this.handleErrorMessage('Formulário inválido. Verifique os campos obrigatórios.');
    }
  }

  private addDoador(formData: any): void {
    this.doadorService['createDoador'](formData).pipe(takeUntil(this.destroy$)).subscribe({
      next: () => {
        this.handleSuccessMessage('Doador criado com sucesso!');
      },
      error: () => {
        this.handleErrorMessage('Erro ao criar doador!');
      }
    });
  }

  private editDoador(formData: any): void {
    const editAction: EditDoadorAction = {
      action: 'edit',
      id: formData.id, // Supondo que o ID seja parte do formulário ou seja recuperado de outra forma
      doadorName: formData.nome // Ajuste conforme necessário para capturar o nome do doador
    };

    this.doadorService['editDoador'](editAction).pipe(takeUntil(this.destroy$)).subscribe({
      next: () => {
        this.handleSuccessMessage('Doador editado com sucesso!');
      },
      error: () => {
        this.handleErrorMessage('Erro ao editar doador!');
      }
    });
  }

  private populateForm(doadorData: GetDoadorResponse): void {
    this.doadorForm.patchValue({
      nome: doadorData.nome,
      cpf: doadorData.cpf,
      rg: doadorData.rg,
      genero: doadorData.genero,
      dataNascimento: doadorData.dataNascimento,
      cnpj: doadorData.cnpj,
      razaoSocial: doadorData.razaoSocial,
      contato1: doadorData.contato1,
      contato2: doadorData.contato2,
      cep: doadorData.cep,
      logradouro: doadorData.logradouro,
      numero: doadorData.numero,
      complemento: doadorData.complemento,
      bairro: doadorData.bairro,
      cidade: doadorData.cidade,
      siglaEstado: doadorData.siglaEstado,
      observacoes: doadorData.observacoes,
      ativo: doadorData.ativo
    });
  }

  private handleSuccessMessage(detail: string): void {
    this.doadorForm.reset();
    this.messageService.add({
      severity: 'success',
      summary: 'Sucesso',
      detail: detail,
      life: 3000
    });
  }

  private handleErrorMessage(detail: string): void {
    this.messageService.add({
      severity: 'error',
      summary: 'Erro',
      detail: detail,
      life: 3000
    });
  }

  ngOnDestroy(): void {
    this.destroy$.next();
    this.destroy$.complete();
  }
}
<div>
  <form [formGroup]="doadorForm" (ngSubmit)="handleSubmit()">
    <div class="card flex flex-column md:flex-column gap-3">
      <input pInputText placeholder="Nome" formControlName="nome" />
      <p-dropdown   style="margin-right: 10px; width: 100% !important;" 
        [options]="tiposPessoa" 
        formControlName="tipoPessoa" 
        placeholder="Selecione o Tipo de Pessoa"
        [options]="estados" 
        formControlName="siglaEstado" 
        placeholder="Selecione um Estado">>
      </p-dropdown>

      <p-dropdown 
        [options]="estados" 
        formControlName="siglaEstado" 
        placeholder="Selecione um Estado">
      </p-dropdown>

      <div *ngIf="doadorForm.get('tipoPessoa')?.value === 'F'">
        <input pInputText style="margin-right: 10px;" placeholder="CPF" formControlName="cpf" />
        <input pInputText style="margin-right: 10px;" placeholder="RG" formControlName="rg" />
        <p-dropdown style="margin-right: 10px !important; "
          [options]="generos" 
          formControlName="genero" 
          placeholder="Selecione o Gênero">
        </p-dropdown>
        <input type="date" tyle="margin-right: 10px;" pInputText placeholder="Data de Nascimento" formControlName="dataNascimento" />
      </div>

      <div *ngIf="doadorForm.get('tipoPessoa')?.value === 'J'">
        <input pInputText placeholder="CNPJ" formControlName="cnpj" />
        <input pInputText placeholder="Razão Social" formControlName="razaoSocial" />
      </div>

      <input pInputText placeholder="Contato 1" formControlName="contato1" />
      <input pInputText placeholder="Contato 2" formControlName="contato2" />
      <input pInputText placeholder="CEP" formControlName="cep" />
      <input pInputText placeholder="Logradouro" formControlName="logradouro" />
      <input pInputText placeholder="Número" formControlName="numero" />
      <input pInputText placeholder="Complemento" formControlName="complemento" />
      <input pInputText placeholder="Bairro" formControlName="bairro" />
      <input pInputText placeholder="Cidade" formControlName="cidade" />


      <textarea pInputTextarea placeholder="Observações" formControlName="observacoes"></textarea>

      <div class="flex flex-row justify-content-center align-content-center align-items-center">
        <p-button [disabled]="!doadorForm.valid" type="submit" label="Concluir"></p-button>
      </div>
    </div>
  </form>
</div>
