import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-beneficiario-form',
  templateUrl: './beneficiario-form.component.html',
  styleUrls: []
})
export class BeneficiarioFormComponent implements OnInit {
  public addBeneficiarioForm: FormGroup;
  public editBeneficiarioForm: FormGroup;
  public beneficiarioAction: any;
  public addBeneficiarioAction = 'ADD_BENEFICIARIO_ACTION';
  public editBeneficiarioAction = 'EDIT_BENEFICIARIO_ACTION';

  public tipoPessoaOptions = [
    { label: 'Física', value: 'F' },
    { label: 'Jurídica', value: 'J' }
  ];

  public generoOptions = [
    { label: 'Masculino', value: 'M' },
    { label: 'Feminino', value: 'F' },
    { label: 'Outro', value: 'O' }
  ];

  constructor(private fb: FormBuilder) {
    this.addBeneficiarioForm = this.fb.group({
      nome: ['', Validators.required],
      datanasc: [null],
      tipopessoa: ['F', Validators.required],
      genero: [null],
      cpf: [''],
      rg: [''],
      cnpj: [''],
      contato1: [''],
      contato2: [''],
      cep: [''],
      logradoudo: [''],
      numero: [''],
      complemento: [''],
      bairro: [''],
      cidade: [''],
      siglaestado: [''],
      observacoes: ['']
    });

    this.editBeneficiarioForm = this.fb.group({
      nome: ['', Validators.required],
      datanasc: [null],
      tipopessoa: ['F', Validators.required],
      genero: [null],
      cpf: [''],
      rg: [''],
      cnpj: [''],
      contato1: [''],
      contato2: [''],
      cep: [''],
      logradoudo: [''],
      numero: [''],
      complemento: [''],
      bairro: [''],
      cidade: [''],
      siglaestado: [''],
      observacoes: ['']
    });
  }

  ngOnInit(): void {
    // Inicialize outras coisas aqui, se necessário
  }

  handleSubmitAddBeneficiario(): void {
    if (this.addBeneficiarioForm.valid) {
      const beneficiarioData = this.addBeneficiarioForm.value;
      console.log('Novo Beneficiário:', beneficiarioData);
      // Aqui você pode emitir um evento ou fazer uma chamada de API
    }
  }

  handleSubmitEditBeneficiario(): void {
    if (this.editBeneficiarioForm.valid) {
      const beneficiarioData = this.editBeneficiarioForm.value;
      console.log('Beneficiário Editado:', beneficiarioData);
      // Aqui você pode emitir um evento ou fazer uma chamada de API
    }
  }
}
<!-- FORMULÁRIO DE EDITAR BENEFICIARIO -->
<div *ngIf="beneficiarioAction?.event?.action === beneficiarioAction">
  <form [formGroup]="editBeneficiarioForm" (ngSubmit)="handleSubmitEditBeneficiario()">
    <div class="card flex flex-column md:flex-column gap-3">
      <input pInputText placeholder="Nome" formControlName="nome" />
      <p-calendar placeholder="Data de Nascimento" formControlName="datanasc"></p-calendar>
      <p-dropdown [options]="tipoPessoaOptions" placeholder="Tipo de Pessoa" formControlName="tipopessoa"></p-dropdown>
      <p-dropdown [options]="generoOptions" placeholder="Gênero" formControlName="genero"></p-dropdown>
      <input pInputText placeholder="CPF" formControlName="cpf" />
      <input pInputText placeholder="RG" formControlName="rg" />
      <input pInputText placeholder="CNPJ" formControlName="cnpj" />
      <input pInputText placeholder="Contato 1" formControlName="contato1" />
      <input pInputText placeholder="Contato 2" formControlName="contato2" />
      <input pInputText placeholder="CEP" formControlName="cep" />
      <input pInputText placeholder="Logradouro" formControlName="logradoudo" />
      <input pInputText placeholder="Número" formControlName="numero" />
      <input pInputText placeholder="Complemento" formControlName="complemento" />
      <input pInputText placeholder="Bairro" formControlName="bairro" />
      <input pInputText placeholder="Cidade" formControlName="cidade" />
      <input pInputText placeholder="Estado" formControlName="siglaestado" />
      <input pInputText placeholder="Observações" formControlName="observacoes" />

      <div class="flex flex-row justify-content-center align-content-center align-items-center m-1">
        <p-button [disabled]="!editBeneficiarioForm.valid" type="submit" label="Concluir"></p-button>
      </div>
    </div>
  </form>
</div>
